import React from "react";
import { translate } from "react-i18next";
import GoogleMapReact from "google-map-react";
import {
  getLatitude,
  getLongitude,
  findLocationByAddress,
  findLocationByLatLng,
  getCurrentLocation,
  updateGeoLocation
} from "../../services/GeolocationService";
import { canUseDOM } from "../../utils";

class ChangeLocation extends React.Component {
  state = {
    address: "",
    location: getCurrentLocation(),
    lat: getLatitude(),
    lng: getLongitude(),
    locationSet: true,
    error: false
  };

  constructor(props) {
    super(props);
    this.updateAddress = this.updateAddress.bind(this);
    this.onGoClick = this.onGoClick.bind(this);
    this.onChooseLocationClick = this.onChooseLocationClick.bind(this);
    this.onGetCurrentLocationClick = this.onGetCurrentLocationClick.bind(this);
    this.navigatorSuccess = this.navigatorSuccess.bind(this);
    this.navigatorError = this.navigatorError.bind(this);
  }

  onChooseLocationClick() {
    updateGeoLocation(this.state);
    this.setState({ locationSet: true });
  }

  updateAddress(event) {
    this.setState({ address: event.target.value });
  }

  onGoClick() {
    this.getLocationByAddress();
  }

  getLocationByAddress() {
    const { address } = this.state;
    if (address) {
      findLocationByAddress(address)
        .then(response => {
          if (
            response.data &&
            response.data.results &&
            response.data.results.length > 0
          ) {
            const firstMatch = response.data.results[0];
            console.log({ location: firstMatch });

            const location = firstMatch.formatted_address;
            const { lat, lng } = firstMatch.geometry.location;
            this.setState({ location });
            this.setState({ lat });
            this.setState({ lng });
            this.setState({ locationSet: false });
            this.setState({ error: false });
          } else {
            console.warn("No results returned from Google Maps API");
            this.setState({ error: true });
          }
        })
        .catch(error => {
          this.setState({ error: true });
          console.error(error);
        });
    }
  }

  onGetCurrentLocationClick() {
    if (canUseDOM) {
      navigator.geolocation.getCurrentPosition(
        this.navigatorSuccess,
        this.navigatorError
      );
    }
  }

  navigatorSuccess(position) {
    this.setState({ lat: position.coords.latitude });
    this.setState({ lng: position.coords.longitude });

    findLocationByLatLng(position.coords.latitude, position.coords.longitude)
      .then(response => {
        if (
          response.data &&
          response.data.results &&
          response.data.results.length > 0
        ) {
          const firstMatch = response.data.results[0];
          console.log({ location: firstMatch });
          const location = firstMatch.formatted_address;
          this.setState({ location });
          this.setState({ locationSet: false });
          this.setState({ error: false });
        } else {
          console.warn("No results returned from Google Maps API");
          this.setState({ error: true });
        }
      })
      .catch(error => {
        this.setState({ error: true });
        console.error(error);
      });
  }

  navigatorError() {
    this.setState({ error: true });
  }

  render() {
    const { zoom, t } = this.props;
    const { address, location, lat, lng, locationSet, error } = this.state;

    const apiKey = process.env.REACT_APP_GOOGLE_API_KEY;
    if (!apiKey) {
      console.error(
        "GOOGLE_API_KEY is missing. Please add it to environment variables. More in readme: https://github.com/Sitecore/Sitecore.HabitatHome.Omni/blob/master/fitness/app/README.md"
      );
    }

    const center = {
      lat: lat,
      lng: lng
    };

    return (
      <div className="changeLocation">
        <div className="changeLocation-search">
          <button
            className="btn btn-secondary active target"
            disabled={canUseDOM && !navigator.geolocation}
            onClick={this.onGetCurrentLocationClick}
          />
          <input
            type="text"
            placeholder={location ? location : t("enter-location")}
            value={address}
            onChange={this.updateAddress}
          />
          <button
            className="btn btn-primary"
            onClick={this.onGoClick}
            disabled={address === ""}
          >
            {t("go")}
          </button>
        </div>

        <div className="changeLocation-map">
          <div className="map">
            <GoogleMapReact
              bootstrapURLKeys={{ key: apiKey }}
              defaultZoom={zoom}
              center={center}
            />
          </div>
          {locationSet ? (
            <button
              className="btn btn-primary btn-action"
              disabled={true}
              onClick={this.onChooseLocationClick}
            >
              {t("location-set")}&nbsp;
              <img
                alt="Location is set"
                src="data:image/svg+xml;utf8;base64,"
              />
            </button>
          ) : (
            <button
              className="btn btn-primary btn-action"
              disabled={error}
              onClick={this.onChooseLocationClick}
            >
              {t("choose-location")}
            </button>
          )}
        </div>
      </div>
    );
  }
}

ChangeLocation.defaultProps = {
  zoom: 11
};

export default translate()(ChangeLocation);
